import { useState } from "react";

function App() {
  const [password, setPassword] = useState("");
  const [isTrue, setIsTrue] = useState(true);

  // const func = () => {
  //   setIsTrue(false);
  // };

  const a = isTrue ? "password" : "text";

  const handleChange = (e) => {
    e.preventDefault();
    setPassword(e.target.value);
  };

  if (
    /[a-zA-Z]/.test(password) &&
    /[0-9]/.test(password) &&
    /[_]/.test(password)
  ) {
    setIsTrue(true);
  }
  return (
    <div className="container">
      <div className="container-stuff">
        /*
        -----------------------------------------------------------------------------------*/
        <div className="email-div">
          <input type="email" placeholder="Enter e-mail" className="email" />
          <p>Please enter a valid email</p>
        </div>
        /*
        ----------------------------------------------------------------------------------*/
        <div className="password-div">
          <input
            placeholder="Enter password"
            className="password"
            onChange={handleChange}
            //type="password"
            type={a}
          />
          <button onClick={() => setIsTrue(!isTrue)}>Show</button>
          <p className="tag">
            Please enter at least 8 charatcer with number, symbol, small and
            capital letter.
          </p>
        </div>
        /*
        ---------------------------------------------------------------------------------------*/
        <div className="password-div">
          <input
            type="password"
            placeholder="Confirm password"
            className="password"
          />
          <button>Show</button>

          <p>Password don't match</p>
        </div>
      </div>
    </div>
  );
}

export default App;
