import { useState } from "react";

function App() {
  const [validation, setValidation] = useState({
    email: "",
    password: "",
    confirmPassword: "",
  });
  const [isTrue, setIsTrue] = useState(true);
  const [isTrue2, setIsTrue2] = useState(true);
  const [a, setA] = useState(true);

  const handleChange = (event) => {
    event.preventDefault();
    const { value, name } = event.target;
    setValidation((prevValue) => {
      return {
        ...prevValue,
        [name]: value,
      };
    });
  };

  const handleClick = () => {
    setA(() => {
      if (
        /[a-z]/.test(validation.password) &&
        /[A-Z]/.test(validation.password) &&
        /[0-9]/.test(validation.password) &&
        /[_]/.test(validation.password)
      ) {
        return false;
      } else {
        true;
      }
    });
  };

  const testingPassword = () => {};
  return (
    <div className="container">
      {validation.email}
      <div className="container-stuff">
        /*
        -----------------------------------------------------------------------------------*/
        <div className="email-div">
          <input
            type="email"
            placeholder="Enter e-mail"
            className="email"
            onChange={handleChange}
            name="email"
          />
          <p>Please enter a valid email</p>
        </div>
        /*
        ----------------------------------------------------------------------------------*/
        <div className="password-div">
          <input
            placeholder="Enter password"
            className="password"
            onChange={handleChange}
            //type="password"
            type={isTrue ? "password" : "text"}
            name="password"
          />
          <button onClick={() => setIsTrue(!isTrue)}>Show</button>

          <p className={a ? "tag" : "tag show-tag"}>
            Please enter at least 8 character with number, symbol, small and
            capital letter.
          </p>
        </div>
        /*
        ---------------------------------------------------------------------------------------*/
        <div className="password-div">
          <input
            type={isTrue2 ? "password" : "text"}
            placeholder="Confirm password"
            className="password"
            onChange={handleChange}
            name="confirmPassword"
          />
          <button onClick={() => setIsTrue2(!isTrue2)}>Show2</button>

          <p>Password don't match</p>
        </div>
        <div>
          <button onClick={handleClick}>Log In</button>
        </div>
      </div>
    </div>
  );
}

export default App;
